// import React from 'react';
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";
import { useState } from "react";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);

  // function handleEditAvatarClick() {
  //   const popupAvatar = document.querySelector(".popup_avatar");
  //   popupAvatar.classList.add("popup_opened");
  // }
  // function handleEditProfileClick() {
  //   const popupProfile = document.querySelector(".popup_profile");
  //   popupProfile.classList.add("popup_opened");
  // }
  // function handleAddPlaceClick() {
  //   const popupCard = document.querySelector(".popup_card");
  //   popupCard.classList.add("popup_opened");
  // }

  function handleEditAvatarClick() {
    setIsEditProfilePopupOpen(true);
  }
  function handleEditProfileClick() {
    setIsAddPlacePopupOpen(true);
  }
  function handleAddPlaceClick() {
    setIsEditAvatarPopupOpen(true);
  }

  return (
    <div className="App">
      <body className="page">
        <Header />

        <Main
          handleEditAvatarClick={handleEditAvatarClick}
          handleEditProfileClick={handleEditProfileClick}
          handleAddPlaceClick={handleAddPlaceClick}
          isEditProfilePopupOpen={isEditProfilePopupOpen}
          isAddPlacePopupOpen={isAddPlacePopupOpen}
          isEditAvatarPopupOpen={isEditAvatarPopupOpen}
        />
        {isEditProfilePopupOpen && (
          <PopupWithForm />
          // name="profile" title="Редактировать профиль">
          //   <input
          //     id="name-input"
          //     type="text"
          //     value=""
          //     placeholder="Имя"
          //     name="name"
          //     required
          //     class="popup__input popup__input_type_name"
          //     minlength="2"
          //     maxlength="40"
          //   />
          //   <span class="popup__input-error name-input-error"></span>
          //   <input
          //     id="profession-input"
          //     type="text"
          //     value=""
          //     placeholder="Профессия"
          //     name="profession"
          //     required
          //     class="popup__input popup__input_type_profession"
          //     minlength="2"
          //     maxlength="200"
          //   />
          //   <span class="popup__input-error profession-input-error"></span>
          // </PopupWithForm>
        )}

        {isAddPlacePopupOpen && (
          <PopupWithForm name="card" title="Новое место">
            <input
              id="place-input"
              type="text"
              placeholder="Название"
              name="place"
              required
              class="popup__input popup__input_type_place"
              minlength="2"
              maxlength="30"
            />
            <span class="popup__input-error place-input-error"></span>
            <input
              id="reference-input"
              type="url"
              placeholder="Ссылка на картинку"
              name="reference"
              required
              class="popup__input popup__input_type_reference"
            />
            <span class="popup__input-error reference-input-error"></span>
          </PopupWithForm>
        )}

        <Footer />

        <PopupWithForm name="confirmation" title="Вы уверены?">
          <button
            type="submit"
            class="popup__submit-button"
            aria-label="согласен"
          >
            Да
          </button>
        </PopupWithForm>
        <PopupWithForm name="avatar" title="Обновить аватар">
          <input
            id="avatar-input"
            type="url"
            placeholder="Ссылка на новый аватар"
            name="avatar"
            required
            class="popup__input popup__input_type_reference"
          />
          <span class="popup__input-error avatar-input-error"></span>
        </PopupWithForm>
        <ImagePopup />
      </body>
    </div>
  );
}

