function Main() {
    return (
        <main className="content">
          <section className="profile">
            <div className="profile__profile-container">
              <img src="#" alt="Аватарка" className="profile__avatar" />
              <img className="profile__avatar-button" onClick={handleEditAvatarClick} />
              <div className="profile__profile-info">
                <div className="profile__profile-info-container">
                  <h1 className="profile__title"></h1>
                  <button type="button" className="profile__edit-button" aria-label="редактировать профиль" onClick={handleEditProfileClick}></button>
                </div>
                <p className="profile__subtitle"></p>
              </div>
            </div>
            <button type="button" className="profile__add-button" aria-label="добавить элемент" onClick={handleAddPlaceClick}></button>
          </section>
          <section className="elements">

          </section>

          <PopupWithForm name='profile' title='Редактировать профиль' />
          <PopupWithForm name='card' title='Новое место' />
          <PopupWithForm name='confirmation' title='Вы уверены?' />
          <PopupWithForm name='avatar' title='Обновить аватар' />

        </main>
    );
  }

  function handleEditAvatarClick() {
    const popupAvatar = document.querySelector('.popup_avatar');
    popupAvatar.classList.add('popup_opened');
  }
  function handleEditProfileClick() {
    const popupProfile = document.querySelector('.popup_profile');
    popupProfile.classList.add('popup_opened');
  }
  function handleAddPlaceClick() {
    const popupCard = document.querySelector('.popup_card');
    popupCard.classList.add('popup_opened');
  }

  export default Main;
