import React from "react";
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";
import { useState } from "react";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isCloseAllPopups, setIsCloseAllPopups] = useState(true);

  // function handleEditAvatarClick() {
  //   const popupAvatar = document.querySelector(".popup_avatar");
  //   popupAvatar.classList.add("popup_opened");
  // }
  // function handleEditProfileClick() {
  //   const popupProfile = document.querySelector(".popup_profile");
  //   popupProfile.classList.add("popup_opened");
  // }
  // function handleAddPlaceClick() {
  //   const popupCard = document.querySelector(".popup_card");
  //   popupCard.classList.add("popup_opened");
  // }

  function handleEditProfileClick() {
    setIsAddPlacePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleEditAvatarClick() {
    setIsEditProfilePopupOpen(true);
  }

  function closeAllPopups() {
    setIsCloseAllPopups(false);
  }

  return (
    <div className="App">
      <div className="page">
        <Header />

        <Main
          // handleEditProfileClick={handleEditProfileClick}
          // handleAddPlaceClick={handleAddPlaceClick}
          // handleEditAvatarClick={handleEditAvatarClick}

          // isEditProfilePopupOpen={isEditProfilePopupOpen}
          // isAddPlacePopupOpen={isAddPlacePopupOpen}
          // isEditAvatarPopupOpen={isEditAvatarPopupOpen}

          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
        />

        {isEditProfilePopupOpen && isCloseAllPopups && (
          <PopupWithForm
            name="profile"
            title="Редактировать профиль"
            isOpen="popup_opened"
            onClose={closeAllPopups}
          >
            <input
              id="name-input"
              type="text"
              value=""
              placeholder="Имя"
              name="name"
              required
              className="popup__input popup__input_type_name"
              // minlength="2"
              // maxlength="40"
            />
            <span className="popup__input-error name-input-error"></span>
            <input
              id="profession-input"
              type="text"
              value=""
              placeholder="Профессия"
              name="profession"
              required
              className="popup__input popup__input_type_profession"
              // minlength="2"
              // maxlength="200"
            />
            <span className="popup__input-error profession-input-error"></span>
          </PopupWithForm>
        )}

        {isAddPlacePopupOpen && isCloseAllPopups && (
          <PopupWithForm
            name="card"
            title="Новое место"
            isOpen="popup_opened"
            onClose={closeAllPopups}
          >
            <input
              id="place-input"
              type="text"
              placeholder="Название"
              name="place"
              required
              className="popup__input popup__input_type_place"
              // minlength="2"
              // maxlength="30"
            />
            <span className="popup__input-error place-input-error"></span>
            <input
              id="reference-input"
              type="url"
              placeholder="Ссылка на картинку"
              name="reference"
              required
              className="popup__input popup__input_type_reference"
            />
            <span className="popup__input-error reference-input-error"></span>
          </PopupWithForm>
        )}

        {isEditAvatarPopupOpen && isCloseAllPopups && (
          <PopupWithForm
            name="avatar"
            title="Обновить аватар"
            isOpen="popup_opened"
            onClose={closeAllPopups}
          >
            <input
              id="avatar-input"
              type="url"
              placeholder="Ссылка на новый аватар"
              name="avatar"
              required
              className="popup__input popup__input_type_reference"
            />
            <span className="popup__input-error avatar-input-error"></span>
          </PopupWithForm>
        )}

        <Footer />

        {/* <PopupWithForm name="confirmation" title="Вы уверены?">
          <button
            type="submit"
            className="popup__submit-button"
            aria-label="согласен"
          >
            Да
          </button>
        </PopupWithForm> */}

        <ImagePopup />
      </div>
    </div>
  );
}

// onEditAvatar = handleEditAvatarClick
// onEditProfile = handleEditProfileClick
// onAddPlace = handleAddPlaceClick

// const isEditProfilePopupOpen =
// const isAddPlacePopupOpen =
// const isEditAvatarPopupOpen =

export default App;
